import { useContext } from 'react'
import {getContrastYIQ} from '../helpers'
import MainContex from './MainContext'
export default function Brand ({brand}) {

    const {selectedBrands,setSelectedBrands} = useContext(MainContex)

    const toggleSelected = () =>{
        if(selectedBrands.includes(brand.slug)){
            setSelectedBrands(selectedBrands.filter(b => b.slug !== brand.slug))
        }else{
            setSelectedBrands([...selectedBrands,brand.slug])
        }
    }

    return ( 
        <div className={`brand ${selectedBrands.includes(brand.slug) ? 'selected' : ''}`}> 
            <h5 onClick={toggleSelected}>{brand.title}</h5>
            <div className="brand-colors">
                {
                    brand.colors.map((color,index) =>(
                        <span style={{"--bgColor": `#${color}`, "--textColor" : `${getContrastYIQ(color)}`}} key={index}>{color}</span>
                    ))
                }
            </div>
        </div>
    )
}
